import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function ProductReviews() {
  const { asin } = useParams(); // 获取产品的唯一 ID (ASIN)
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchProductReviews = async () => {
      const apiKey = process.env.REACT_APP_AMAZON_API_KEY;
      const apiHost = process.env.REACT_APP_AMAZON_API_HOST;

      try {
        const response = await fetch(
          `https://${apiHost}/product/${asin}/reviews`,
          {
            method: "GET",
            headers: {
              "x-rapidapi-key": apiKey,
              "x-rapidapi-host": apiHost,
            },
          }
        );
        if (response.ok) {
          const data = await response.json();
          setReviews(data.reviews);
        } else {
          console.error("Failed to fetch product reviews");
        }
      } catch (error) {
        console.error("Error fetching product reviews:", error);
      }
    };

    fetchProductReviews();
  }, [asin]);

  return (
    <div className="product-reviews">
      <h1>Product Reviews</h1>
      {reviews.length === 0 ? (
        <p>No reviews available.</p>
      ) : (
        <ul>
          {reviews.map((review, index) => (
            <li key={index}>
              <p>{review.text}</p>
              <p>Rating: {review.rating}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
