import React, { useEffect, useState } from "react";
import { useAuthUser } from "../security/AuthContext";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const { isAuthenticated } = useAuthUser();

  useEffect(() => {
    if (isAuthenticated) {
      fetchCartItems();
    } else {
      alert("Please log in to view the cart.");
    }
  }, [isAuthenticated]);

  const fetchCartItems = async () => {
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/cart`, {
        credentials: "include",
      });
      if (res.ok) {
        const data = await res.json();
        setCartItems(data);
      } else {
        console.error("Failed to fetch cart items");
      }
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  };

  const handleUpdateQuantity = async (id, quantity) => {
    if (quantity < 1) return;
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/cart/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ quantity }),
        credentials: "include",
      });
      if (res.ok) {
        setCartItems((prev) =>
          prev.map((item) => (item.id === id ? { ...item, quantity } : item))
        );
      } else {
        console.error("Failed to update item quantity");
      }
    } catch (error) {
      console.error("Error updating item quantity:", error);
    }
  };

  const handleDeleteItem = async (id) => {
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/cart/${id}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (res.ok) {
        setCartItems((prev) => prev.filter((item) => item.id !== id));
      } else {
        console.error("Failed to delete item from cart");
      }
    } catch (error) {
      console.error("Error deleting item from cart:", error);
    }
  };

  const handleCheckout = () => {
    alert("Proceeding to checkout!");
  };

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      <ul>
        {cartItems.map((item) => (
          <li key={item.id}>
            {item.product.name} - Quantity: {item.quantity}
            <button onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}>+</button>
            <button onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}>-</button>
            <button onClick={() => handleDeleteItem(item.id)}>Remove</button>
          </li>
        ))}
      </ul>
      {cartItems.length > 0 && (
        <button onClick={handleCheckout} className="checkout-button">
          Checkout
        </button>
      )}
    </div>
  );
}
