import { useAuthUser } from "../security/AuthContext";
import { useNavigate, Outlet, Link } from "react-router-dom";
import ProductsList from "../components/ProductsList"; // Import the ProductsList component

export default function AppLayout() {
  const { user, logout } = useAuthUser();
  const navigate = useNavigate();

  return (
    <div className="app">
      <div className="title">
        <h1>NEU Online Store</h1>
      </div>
      <div className="header">
        <nav className="menu">
          <ul className="menu-list">
            <li>
              <Link to="/app">Profile</Link>
            </li>
            <li>
              <Link to="/app/cart">Cart</Link>
            </li>
            <li>
              <Link to="/app/amazon-products">Amazon Products</Link>
            </li>
            {user ? (
              <li>
                <button
                  className="exit-button"
                  onClick={async () => {
                    await logout();
                    navigate("/");
                  }}
                >
                  LogOut
                </button>
              </li>
            ) : (
              <>
                <li>
                  <Link to="/login">Login</Link>
                </li>
                <li>
                  <Link to="/register">Create Account</Link>
                </li>
              </>
            )}
          </ul>
        </nav>
        {user && <div>Welcome ðŸ‘‹ {user.name}</div>}
      </div>
      <div className="content">
        {/* Always show ProductsList component here on the main page */}
        <ProductsList />
        {/* Render other nested routes here */}
        <Outlet />
      </div>
    </div>
  );
}
