import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuthUser } from "../security/AuthContext";
import ProductsList from './ProductsList';
import ProductInsertion from './ProductInsertion';
import ProductSearch from './ProductSearch';
import Cart from './Cart';
import Profile from './Profile';

export default function AppLayout() {
  const { user, logout } = useAuthUser();
  const navigate = useNavigate();
  const [activeModule, setActiveModule] = useState('products');

  const handleLogout = async () => {
    await logout();
    navigate('/');
  };

  const renderModule = () => {
    switch (activeModule) {
      case 'products':
        return <ProductsList />;
      case 'insert':
        return <ProductInsertion />;
      case 'amazon':
        return <ProductSearch />;
      case 'cart':
        return <Cart />;
      case 'profile':
        return <Profile />;
      default:
        return <ProductsList />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex-shrink-0">
              <h1 className="text-xl font-bold">NEU Online Store</h1>
            </div>

            {/* Navigation */}
            <nav className="flex space-x-8">
              <button 
                onClick={() => setActiveModule('products')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  activeModule === 'products' ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                Products
              </button>
              <button 
                onClick={() => setActiveModule('cart')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  activeModule === 'cart' ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                Cart
              </button>
              <button 
                onClick={() => setActiveModule('amazon')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  activeModule === 'amazon' ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                Amazon Search
              </button>
              <button 
                onClick={() => setActiveModule('insert')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  activeModule === 'insert' ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                Insert Product
              </button>
              <button 
                onClick={() => setActiveModule('profile')}
                className={`px-3 py-2 rounded-md text-sm font-medium ${
                  activeModule === 'profile' ? 'bg-gray-900 text-white' : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                Profile
              </button>
              {user && (
                <button
                  onClick={handleLogout}
                  className="px-3 py-2 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-100"
                >
                  Logout
                </button>
              )}
            </nav>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {user && <div className="mb-4 text-sm text-gray-600">Welcome ðŸ‘‹ {user.name}</div>}
        <div className="bg-white shadow rounded-lg p-6">
          {renderModule()}
        </div>
      </main>
    </div>
  );
}
