import React from "react";
import * as ReactDOMClient from "react-dom/client";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import Profile from "./components/Profile";
import ProductsList from "./components/ProductsList";
import ProductInsertion from "./components/ProductInsertion";
import AppLayout from "./components/AppLayout";
import Login from "./components/Login";
import Register from "./components/Register";
import NotFound from "./components/NotFound";
import Home from "./components/Home";
import Cart from "./components/Cart";
import ProductSearch from "./components/ProductSearch";
import ProductDetails from "./components/ProductDetails";
import ProductReviews from "./components/ProductReviews";
import { AuthProvider } from "./security/AuthContext";
import RequireAuth from "./security/RequireAuth";
import "./style/normalize.css";
import "./style/index.css";

const container = document.getElementById("root");
const root = ReactDOMClient.createRoot(container);

root.render(
  <AuthProvider>
    <BrowserRouter>
      <Routes>
        {/* Home page now displays both ProductsList and login/register links */}
        <Route
          path="/"
          element={
            <div>
              <ProductsList />
              <div className="auth-links">
                <Link to="/login">Login</Link> | <Link to="/register">Register</Link>
              </div>
            </div>
          }
        />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        <Route
          path="/app/*"
          element={
            <RequireAuth>
              <AppLayout />
            </RequireAuth>
          }
        >
          <Route index element={<Profile />} />
          <Route path="products/insert" element={<ProductInsertion />} />
          <Route path="cart" element={<Cart />} />
          <Route path="amazon-products" element={<ProductSearch />} />
          <Route path="product/:asin" element={<ProductDetails />} />
          <Route path="product/:asin/reviews" element={<ProductReviews />} />
        </Route>

        <Route path="*" element={<NotFound />} />
      </Routes>
    </BrowserRouter>
  </AuthProvider>
);
